import { createSlice, PayloadAction, createAsyncThunk } from '@reduxjs/toolkit'
import axios from 'axios'

export interface CounterState {
  value: number
}
type WeatherInf = {
  message: string
}

export const fetchWeather = createAsyncThunk<
  WeatherInf,
  undefined,
  { rejectValue: string }
>(
  'todos/fetch',

  async (_, { rejectWithValue }) => {
    const res = await axios.get(
      `https://631f574722cefb1edc498d47.mockapi.io/items?limit=4`
    )
    if (res.data.length === 0) return rejectWithValue('Failed to fetch todos.')
    console.log(res)
    return res.data
  }
)

const initialState: CounterState = {
  value: 0,
}

export const counterSlice = createSlice({
  name: 'counter',
  initialState,
  reducers: {
    changeIncrementAmount: (state, action: PayloadAction<number>) => {},
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchWeather.fulfilled, (state, action) => {
        console.log(action.payload)
      })
      .addCase(fetchWeather.rejected, (state, action) => {
        console.log(action.payload)
      })
  },
})

export const { changeIncrementAmount } = counterSlice.actions

export default counterSlice.reducer
